import React, { Component } from 'react'
import { List, Tag,Carousel, WingBlank } from 'antd-mobile';
import '../components/Cin'
const Item = List.Item;
const Brief = Item.Brief;
export default class Cinema extends Component {
    constructor(props) {
        super(props)
        this.state = {
            item: this.props.location.query,
            data: ['1', '2', '3'],
            imgHeight: 176,
        }
    }
    componentDidMount() {
        console.log(this.state.item);
        setTimeout(() => {
            this.setState({
              data: ['AiyWuByWklrrUDlFignR', 'TekJlZRVCjLFexlOCuWn', 'IJOtIlfsYdTyaDTRVrLI'],
            });
          }, 100);
    }
    render() {
        return (
            <div>
                <List className="my-list">
                    <Item multipleLine >
                        <span style={{ fontWeight: 'bold' }}>
                            {this.state.item.cinema}
                        </span> <Brief>{this.state.item.address}</Brief>
                        <Tag small style={{ color: '#FF2E62' }}>{this.state.item.Tag1 == 1 ? "可领影城劵" : ""}</Tag>
                        <Tag small style={{ color: '#FF9C1C' }}>{this.state.item.Tag2 == 1 ? "特价票" : ""}</Tag>
                        <Tag small>{this.state.item.Tag3 == 1 ? "vip厅" : "普通厅"}</Tag>
                        <Tag small>{this.state.item.Tag4 == 1 ? "可停车" : "不可停车"}</Tag>
                    </Item>
                </List>
                <WingBlank>
        <Carousel className="space-carousel"
          frameOverflow="visible"
          cellSpacing={10}
          slideWidth={0.8}
          autoplay
          infinite
          beforeChange={(from, to) => console.log(`slide from ${from} to ${to}`)}
          afterChange={index => this.setState({ slideIndex: index })}
        >
          {this.state.data.map((val, index) => (
            <a
              key={val}
              style={{
                display: 'block',
                position: 'relative',
                top: this.state.slideIndex === index ? -10 : 0,
                height: this.state.imgHeight,
                boxShadow: '2px 1px 1px rgba(0, 0, 0, 0.2)',
              }}
            >
              <img
                src={`https://zos.alipayobjects.com/rmsportal/${val}.png`}
                alt=""
                style={{ width: '100%', verticalAlign: 'top' }}
                onLoad={() => {
                  // fire window resize event to change height
                  window.dispatchEvent(new Event('resize'));
                  this.setState({ imgHeight: 'auto' });
                }}
              />
            </a>
          ))}
        </Carousel>
      </WingBlank>
            </div>
        )
    }
}
