import React from 'react'
import cookie from 'react-cookies'
import $ from 'jquery';
import { TabBar } from 'antd-mobile';
import TabsS from './TabsS';
export default class TabBarS extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedTab: 'mineTab',
    };
  }
  Islogin(){
    console.log("aaaa")
    if(this.state.selectedTab=='mineTab'){
      this.props.history.push('/login')
    }
  }
  login() {
    let login=document.getElementsByName('user')[1].value;
    let password=document.getElementsByName("password")[1].value;
    console.log(login);
    console.log(password);
    $.ajax({
        url: "http://192.168.7.142:9000/login",
        type: "POST",
        data: {
            loginid:login,
            pwd:password
        },
        dataType: "text",
        success(user) {
            if(user){
                user = JSON.parse(user);
                console.log(user);
                cookie.save('myuser', {id:user.id,name:user.username}, { path: '/' });
            }
            else{
                console.log("帐号或密码错误")
            }
            
        },
        error() {
            
        }
    })
    this.props.history.push('/');
}

  renderContent() {
      if (this.state.selectedTab==="hotTab") {
        
        return (
            <div style={{ backgroundColor: 'white', height: '100%', textAlign: 'center' }}>
                <TabsS></TabsS>
            </div>
          );
      }
      else if (this.state.selectedTab==="movieTab") {
         
        return (
            <div style={{ backgroundColor: 'white', height: '100%', textAlign: 'center' }}>
            </div>
          );
      }
      else {
         
        let user = cookie.load('myuser')
        if(user) {
          return (
              <div style={{ backgroundColor: 'white', height: '100%', textAlign: 'center' }}>
                这是登录后的界面
              </div>
            );
        }else {
          return (
            <div style={{ backgroundColor: 'white', height: '100%', textAlign: 'center' }}>
            <div className="bank"></div>
            <div className="logo"></div>
            <div className='input'>
              <input type="text" className="user" name="user"placeholder="请输入账号" /><br/>
              <input type="password" className="password" name="password"placeholder="请输入密码" id=""/>
            </div>
            <button type="submit" className="login" onClick={this.login.bind(this )}>登录</button>
            </div>
          );
        }
      }
      
  }

  render() {
    return (
      <div style={{position: 'fixed', height: '100%', width: '100%', top: 0}} >
        <TabBar
          unselectedTintColor="#949494"
          tintColor="#33A3F4"
          barTintColor="white"
        >
          <TabBar.Item
            title="热映"
            key="热映"
            icon={<div style={{
              width: '22px',
              height: '22px',
              background: 'url("http://gw.alicdn.com/tfs/TB1aNR5ifDH8KJjy1XcXXcpdXXa-66-66.png") center center /  21px 21px no-repeat' }}
            />
            }
            selectedIcon={<div style={{
              width: '22px',
              height: '22px',
              background: 'url("http://gw.alicdn.com/tfs/TB1L.8XilfH8KJjy1XbXXbLdXXa-66-66.png") center center /  21px 21px no-repeat' }}
            />
            }
            selected={this.state.selectedTab === 'hotTab'}
            badge={1}
            onPress={() => {
              this.setState({
                selectedTab: 'hotTab',
              });
            }}
            data-seed="logId"
          >
            {this.renderContent('hotTab')}
          </TabBar.Item>
          <TabBar.Item
            icon={
              <div style={{
                width: '22px',
                height: '22px',
                background: 'url("http://gw.alicdn.com/tfs/TB1ScFoidzJ8KJjSspkXXbF7VXa-66-66.png") center center /  21px 21px no-repeat' }}
              />
            }
            selectedIcon={
              <div style={{
                width: '22px',
                height: '22px',
                background: 'url(https://gw.alicdn.com/tfs/TB1kDeeib_I8KJjy1XaXXbsxpXa-66-66.png) center center /  21px 21px no-repeat' }}
              />
            }
            title="影院"
            key="影院"
            dot
            selected={this.state.selectedTab === 'movieTab'}
            onPress={() => {
              this.setState({
                selectedTab: 'movieTab',
              });
            }}
          >
            {this.renderContent('movieTab')}
          </TabBar.Item>
         < TabBar.Item
            icon={{ uri: 'https://gw.alicdn.com/tfs/TB1_Ox9invI8KJjSspjXXcgjXXa-66-66.png' }}
            selectedIcon={{ uri: 'https://gw.alicdn.com/tfs/TB16z4AicrI8KJjy0FhXXbfnpXa-66-66.png' }}
            title="我的"
            key="我的"
            selected={this.state.selectedTab === 'mineTab'}
            onPress={() => {
              this.setState({
                selectedTab: 'mineTab',
              });
            }}
          >
            {this.renderContent('mineTab')}
          </TabBar.Item>
        </TabBar>
      </div>
    );
  }
}
