import React, { Component } from 'react'
import $ from 'jquery';
export default class MovieDetail extends Component {
    constructor(props){
        super(props)
        this.state={
            detail:[]
        }
    }
    componentDidMount(){
        let that=this;
        let title=this.props.location.query.title
        $.ajax({
            url: "http://localhost:9000/finded",
            type: "GET",
            data:{
                title:title
            },
            dataType: "text",
            success(data) {
                if (data) {
                    data = JSON.parse(data);
                    that.setState({
                        detail:data
                    })
                  
                }
            },
            error() {
  
            }
        });
    }
    render() {
        return (
            <div>
            </div>
        )
    }
}
