import React, { Component } from 'react'
import { List, Tag } from 'antd-mobile';
const Item = List.Item;
const Brief = Item.Brief;
export default class Cinema extends Component {
    constructor(props) {
        super(props)
        this.state = {
            item: this.props.location.query
        }
    }
    componentDidMount() {
        console.log(this.state.item);
    }
    render() {
        return (
            <div>
                <List className="my-list">
                    <Item multipleLine >
                        <span style={{ fontWeight: 'bold' }}>
                            {this.state.item.cinema}
                        </span> <Brief>{this.state.item.address}</Brief>
                        <Tag small style={{ color: '#FF2E62' }}>{this.state.item.Tag1 == 1 ? "可领影城劵" : ""}</Tag>
                        <Tag small style={{ color: '#FF9C1C' }}>{this.state.item.Tag2 == 1 ? "特价票" : ""}</Tag>
                        <Tag small>{this.state.item.Tag3 == 1 ? "vip厅" : "普通厅"}</Tag>
                        <Tag small>{this.state.item.Tag4 == 1 ? "可停车" : "不可停车"}</Tag>
                    </Item>
                </List>
            </div>
        )
    }
}
