import React, { Component } from 'react'
import { List ,Tag} from 'antd-mobile';
import './Movie.css'
const Item = List.Item;
const Brief = Item.Brief;
export default class movie extends Component {
    constructor(){
        super()
        this.state={
            list:[
                {  id:1,
                   cinema:"重庆博纳电影院",
                   minprice:19.9,
                   address:"沙坪坝区凤天大道",
                   Tag1:1,
                   Tag2:1,
                   Tag3:0,
                   Tag4:1,
                }
            ]
        }
    }
    List(){
        const listItems = this.state.list.map((item) =>{
                return  (
                <div key={item.id}>
                <div style={{ width: '100%', height: '3rem' }}></div>
                <List renderHeader={() => 'Align Vertical Center'} className="my-list">
                    <Item multipleLine extra={item.minprice}>
                        {item.cinema} <Brief>{item.address}</Brief>
                        <Tag small style={{color:'#FF2E62'}}>{item.Tag1==1?"可领影城劵":""}</Tag>
                        <Tag small style={{color:'#FF9C1C'}}>{item.Tag2==1?"特价票":""}</Tag>
                        <Tag small>{item.Tag2==1?"":""}</Tag>
                        <Tag small>可停车</Tag>
                    </Item>
                </List>
                </div>
                )
        }
        );
        return listItems
    }
    render() {
        return (
            <div>{this.List()}
            </div>
        )
    }
}
