import React, { Component } from 'react'
import { List, Tag, Carousel, WingBlank } from 'antd-mobile';
import $ from 'jquery';
import '../components/Cinema.css'
const Item = List.Item;
const Brief = Item.Brief;
export default class Cinema extends Component {
  constructor(props) {
    super(props)
    this.state = {
      // item: this.props.location.query,
      item: {
        id: 1,
        cinname: "重庆博纳电影院",
        price: 19.9,
        address: "沙坪坝区凤天大道37号（第三军医大对面）",
        act1: 1,
        act2: 1,
        act3: 0,
        act4: 1,
      },
      data: [
        'https://gw.alicdn.com/i4/TB1nW_EnUz1gK0jSZLeXXb9kVXa_.jpg_600x600Q30.jpg_.webp',
        'https://gw.alicdn.com/i2/TB1vmuLnAT2gK0jSZFkXXcIQFXa_.jpg_600x600Q30.jpg_.webp',
        'https://gw.alicdn.com/i1/TB1wFDFnUT1gK0jSZFrXXcNCXXa_.jpg_600x600Q30.jpg_.webp',
        'https://gw.alicdn.com/i3/TB1QhPwlYj1gK0jSZFOXXc7GpXa_.jpg_600x600Q30.jpg_.webp'
      ],
      data2: []
    }
  }
  componentDidMount() {
    let that = this;
    let id = this.state.item.id;
    $.ajax({
      url: "http://localhost:9000/cplayplan",
      type: "GET",
      data: {
        cid: id
      },
      dataType: "text",
      success(data) {
        if (data) {
          data = JSON.parse(data);
          that.setState({
            data2: data
          })
          console.log(that.state.data2);
        }
        else {
        }
      },
      error() {

      }
    })
  }
  list() {
    const listItems = this.state.data2.map((item) => {

      return (
        <div className="play">
          <List renderHeader={() => 'Align Vertical Center'} className="my-list">
            <Item multipleLine extra="￥29.9">
              10:10 <Brief>12:11散场</Brief>
            </Item>
            <div>电影</div>
            <div></div>
          </List>
        </div>
      )
    }
    );
    return listItems
  }
  render() {
    return (
      <div>
        <List className="my-list">
          <Item multipleLine >
            <span style={{ fontWeight: 'bold' }}>
              {this.state.item.cinname}
            </span> <Brief>{this.state.item.address}</Brief>
            <Tag small style={{ color: '#FF2E62' }}>{this.state.item.act1 == 1 ? "可领影城劵" : ""}</Tag>
            <Tag small style={{ color: '#FF9C1C' }}>{this.state.item.act2 == 1 ? "特价票" : ""}</Tag>
            <Tag small>{this.state.item.act3 == 1 ? "vip厅" : "普通厅"}</Tag>
            <Tag small>{this.state.item.act4 == 1 ? "可停车" : "不可停车"}</Tag>
          </Item>
        </List>
        <WingBlank>
          <Carousel className="space-carousel"
            frameOverflow="visible"
            cellSpacing={10}
            slideWidth={0.8}
            autoplay
            infinite
            beforeChange={(from, to) => console.log(`slide from ${from} to ${to}`)}
            afterChange={index => this.setState({ slideIndex: index })}
          >
            {this.state.data.map((val, index) => (
              <a
                key={val}
                style={{
                  display: 'block',
                  position: 'relative',
                  top: this.state.slideIndex === index ? -10 : 0,
                  height: this.state.imgHeight,
                  boxShadow: '2px 1px 1px rgba(0, 0, 0, 0.2)',
                }}
              >
                <img
                  src={`${val}`}
                  alt=""
                  style={{ width: '100%', verticalAlign: 'top' }}
                  onLoad={() => {
                    window.dispatchEvent(new Event('resize'));
                    this.setState({ imgHeight: 'auto' });
                  }}
                />
              </a>
            ))}
          </Carousel>
        </WingBlank>
        <div>
          {this.list()}
        </div>
      </div>
    )
  }
}
