import React, { Component } from 'react'
import $ from 'jquery';
import { List, Tag, Popover, NavBar, Icon } from 'antd-mobile';
import './Movie.css'
const Item = List.Item;
const Brief = Item.Brief;
const PopoverItem = Popover.Item;
export default class movie extends Component {
    constructor(props) {
        super(props)
        this.state = {
            homeobj: props.homeobj,
            visible: false,
            selected: '',
            list: [
                // {
                //     id: 1,
                //     cinema: "重庆博纳电影院",
                //     minprice: 19.9,
                //     address: "沙坪坝区凤天大道37号（第三军医大对面）",
                //     Tag1: 1,
                //     Tag2: 1,
                //     Tag3: 0,
                //     Tag4: 1,
                // },
                // {
                //     id: 2,
                //     cinema: "UME影城（重庆北碚店）",
                //     minprice: 16.5,
                //     address: "北碚区康宁路58号",
                //     Tag1: 1,
                //     Tag2: 0,
                //     Tag3: 0,
                //     Tag4: 1,
                // },
                // {
                //     id: 3,
                //     cinema: "华谊兄弟电影（南坪店）",
                //     minprice: 25.5,
                //     address: "南岸区南坪西路38号",
                //     Tag1: 1,
                //     Tag2: 1,
                //     Tag3: 0,
                //     Tag4: 1,
                // }
            ]
        }
    }

    componentDidMount() {
        let that = this;
        $.ajax({
            url: "http://localhost:9000/A",
            type: "GET",
            dataType: "text",
            success(data) {
                if (data) {
                    data = JSON.parse(data);
                    that.setState({
                        list: data
                    })
                    console.log(data);
                }
                else {
                }
            },
            error() {

            }
        })
    }
    //点击某一项的回调函数
    onSelect(opt) {
        console.log(opt.props.value);
        //按价格升序的方式给电影院排序
        if (opt.props.value == "价格最低") {
            let arr = this.state.list;
            arr.sort(function (obj1, obj2) {
                let price1 = obj1.minprice;
                let price2 = obj2.minprice;
                if (price1 < price2) {
                    return -1;
                } else if (price1 > price2) {
                    return 1;
                } else {
                    return 0;
                }

            })
            this.setState({
                visible: false,
                selected: opt.props.value,
                list: arr
            });
        }
        //筛选出有特价票的电影院
        else if (opt.props.value == "特价票") {
            let arr = this.state.list;
            let result = [];
            let i = 0;
            while (i < arr.length) {
                if (arr[i].Tag2 == 1) {
                    result.push(arr[i]);
                }
                i++;
            }
            this.setState({
                visible: false,
                selected: opt.props.value,
                list: result
            });
        }
        //筛选出可以停车的电影院
        else {
            let arr = this.state.list;
            let result = [];
            let i = 0;
            while (i < arr.length) {
                if (arr[i].Tag4 == 1) {
                    result.push(arr[i]);
                }
                i++;
            }
            this.setState({
                visible: false,
                selected: opt.props.value,
                list: result
            });
        }
    };
    toCinema(item) {
        console.log(item);
        this.state.homeobj.props.history.push({
            pathname: "/cinema",
            query: {
                id: item.id,
                cinema: item.cinema,
                address: item.address,
                Tag1: item.Tag1,
                Tag2: item.Tag2,
                Tag1: item.Tag1,
                Tag1: item.Tag1,
            }
        });
    }
    //列表渲染
    List() {
        const listItems = this.state.list.map((item) => {
            return (
                <div key={item.id} onClick={this.toCinema.bind(this, item)}>
                    <List renderHeader={() => 'Align Vertical Center'} className="my-list">
                        <Item multipleLine extra={item.minprice + "起"}>
                            {item.cinema} <Brief>{item.address}</Brief>
                            <Tag small style={{ color: '#FF2E62' }}>{item.Tag1 == 1 ? "可领影城劵" : ""}</Tag>
                            <Tag small style={{ color: '#FF9C1C' }}>{item.Tag2 == 1 ? "特价票" : ""}</Tag>
                            <Tag small>{item.Tag3 == 1 ? "vip厅" : "普通厅"}</Tag>
                            <Tag small>{item.Tag4 == 1 ? "可停车" : "不可停车"}</Tag>
                        </Item>
                    </List>
                </div>
            )
        }
        );
        return listItems
    }
    render() {
        return (
            <div>
                <div style={{ width: '100%', height: '3rem' }}>
                    <NavBar
                        mode="light"
                        rightContent={
                            <Popover mask
                                overlayClassName="fortest"
                                overlayStyle={{ color: 'currentColor' }}
                                visible={this.state.visible}
                                overlay={[
                                    (<PopoverItem key="4" value="特价票" data-seed="logId">特价票</PopoverItem>),
                                    (<PopoverItem key="5" value="价格最低" style={{ whiteSpace: 'nowrap' }}>价格最低</PopoverItem>),
                                    (<PopoverItem key="6" value="可停车" >
                                        <span style={{ marginRight: 5 }}>可停车</span>
                                    </PopoverItem>),
                                ]}
                                align={{
                                    overflow: { adjustY: 0, adjustX: 0 },
                                    offset: [-10, 0],
                                }}
                                // onVisibleChange={this.handleVisibleChange}
                                onSelect={this.onSelect.bind(this)}
                            >
                                <div style={{
                                    height: '100%',
                                    padding: '0 15px',
                                    marginRight: '-15px',
                                    display: 'flex',
                                    alignItems: 'center',
                                }}
                                >
                                    <Icon type="ellipsis" />
                                </div>
                            </Popover>
                        }
                    >
                        电影院
      </NavBar>
                </div>
                {this.List()}
            </div>
        )
    }
}
