import React, { Component } from 'react'
import { Tabs, Badge } from 'antd-mobile';
import $ from 'jquery';
import './HotShow.css';
import List from './List'
export default class HotShow extends Component {
    constructor() {
        super();
        this.state = {
            data:null
        }
    }
    componentDidMount() {
        let that = this;
        $.ajax({
            url: "http://localhost:9000/getindex",
            type: "GET",
            dataType: "text",
            success(data) {
                if (data) {
                    data = JSON.parse(data);
                    that.setState({
                        data: data
                    })
                    console.log(that.state.data);
                }
            },
            error() {

            }
        })
    }
    tabExample() {
        const tabs = [
            { title: <Badge text={'3'}>正在热映</Badge> },
            { title: <Badge text={'4'}>即将上映</Badge> }
        ];
        //所有的电影列表
        let movielist = this.state.data;

        //已经上映的电影
        let list1 = [];
        //未上映的电影
        let list2 = [];
        let year = new Date().getFullYear();
        let month = new Date().getMonth() + 1;
        let date = new Date().getDate();
        // console.log(year)
        if (movielist!=null) {
            console.log(movielist);
            for (let i = 0; i < movielist.length; i++) {
                let movieYear= Number( movielist[i].moviedate.substring(0,4));
                let movieMonth=Number( movielist[i].moviedate.substring(4,6));
                let moviedate= Number( movielist[i].moviedate.substring(7,9));
                if(movieYear>year){
                    list2.push(movielist[i]);
                }
                else if(movieYear<year){
                    list1.push(movielist[i]);
                }
                else{
                    if (movieMonth>month) {
                        list2.push(movielist[i]);
                    }
                }
            }
        }
        return (
            <div>
                <Tabs tabs={tabs}
                    initialPage={0}
                    onChange={(tab, index) => { console.log('onChange', index, tab); }}
                    onTabClick={(tab, index) => { console.log('onTabClick', index, tab); }}
                >
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: 'auto', backgroundColor: '#fff' }}>
                        <List isShow={true}></List>
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: 'auto', backgroundColor: '#fff' }}>
                        <List isShow={false}></List>
                    </div>
                </Tabs>

            </div>
        )
    }
    render() {
        return this.tabExample()
    }
}
