import React, { Component } from 'react'
import { List ,Tag} from 'antd-mobile';
import './Movie.css'
const Item = List.Item;
const Brief = Item.Brief;
export default class movie extends Component {
    constructor(){
        super()
        this.state={
            list:[
                {  id:1,
                   cinema:"重庆博纳电影院",
                   minprice:19.9,
                   address:"沙坪坝区凤天大道37号（第三军医大对面）",
                   Tag1:1,
                   Tag2:1,
                   Tag3:0,
                   Tag4:1,
                },
                {  id:2,
                    cinema:"UME影城（重庆北碚店）",
                    minprice:16.5,
                    address:"北碚区",
                    Tag1:1,
                    Tag2:1,
                    Tag3:0,
                    Tag4:1,
                 },
                 {  id:2,
                    cinema:"重庆博纳电影院",
                    minprice:19.9,
                    address:"沙坪坝区凤天大道37号（第三军医大对面）",
                    Tag1:1,
                    Tag2:1,
                    Tag3:0,
                    Tag4:1,
                 }
            ]
        }
    }
    List(){
        const listItems = this.state.list.map((item) =>{
                return  (
                <div key={item.id}>
                <List renderHeader={() => 'Align Vertical Center'} className="my-list">
                    <Item multipleLine extra={item.minprice}>
                        {item.cinema} <Brief>{item.address}</Brief>
                        <Tag small style={{color:'#FF2E62'}}>{item.Tag1==1?"可领影城劵":""}</Tag>
                        <Tag small style={{color:'#FF9C1C'}}>{item.Tag2==1?"特价票":""}</Tag>
                        <Tag small>{item.Tag3==1?"vip厅":"普通厅"}</Tag>
                        <Tag small>{item.Tag4==1?"可停车":"不可停车"}</Tag>
                    </Item>
                </List>
                </div>
                )
        }
        );
        return listItems
    }
    render() {
        return (
            <div> 
                <div style={{ width: '100%', height: '3rem' }}></div>
                {this.List()}
            </div>
        )
    }
}
