import React, { Component } from 'react'
import { List ,Tag} from 'antd-mobile';
import './Movie.css'
const Item = List.Item;
const Brief = Item.Brief;
export default class movie extends Component {
    constructor(){
        super()
        this.state={
            list:[
                {
                   cinema:重庆博纳电影院,
                   minprice:19.9,
                   address
                }
            ]
        }
    }
    render() {
        return (
            <div>
                <div style={{ width: '100%', height: '3rem' }}></div>
                <List renderHeader={() => 'Align Vertical Center'} className="my-list">
                    <Item multipleLine extra="19.9起">
                        重庆博纳电影院 <Brief>沙坪坝区凤天大道</Brief>
                        <Tag small style={{color:'#FF2E62'}}>可领影城卷</Tag>
                        <Tag small style={{color:'#FF9C1C'}}>特价票</Tag>
                        <Tag small>vip厅</Tag>
                        <Tag small>可停车</Tag>
                    </Item>
                    
                </List>

            </div>
        )
    }
}
