import React from 'react';
import './List.css'
import { ListView } from 'antd-mobile';
import $ from 'jquery';
import IsPurser from './IsPurser';
const dataSource = new ListView.DataSource({
    rowHasChanged: (row1, row2) => row1 !== row2,
});
// const NUM_ROWS = 20;
// let pageIndex = 0;

// function genData(pIndex = 0) {
//     const dataBlob = {};
//     for (let i = 0; i < NUM_ROWS; i++) {
//         const ii = (pIndex * NUM_ROWS) + i;
//         dataBlob[`${ii}`] = `row - ${ii}`;
//     }
//     return dataBlob;
// }

export default class List extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            dataSource,
            isLoading: false,
            isShow: props.isShow,
            NUM_ROWS: 20,
            pageIndex: 0,
            data1: null,
            data: [
                {
                    movieimage: 'https://gw.alicdn.com/i1/TB1J0Hkk.Y1gK0jSZFMXXaWcVXa_.jpg_160x160Q30s100.jpg_.webp',
                    movietitle: '冰雪奇缘2',
                    moviemark: '暂无评分',
                    moviedirect: '巴克',
                    moviestar: '贝尔',
                    id: '1'
                },
                {
                    movieimage: 'https://gw.alicdn.com/i4/TB1gxsmmhz1gK0jSZSgXXavwpXa_.jpg_160x160Q30s100.jpg_.webp',
                    movietitle: '你是凶手',
                    moviemark: '8.2',
                    moviedirect: '王立',
                    moviestar: '王千源 宋佳',
                    id: '2'
                },
                {
                    movieimage: 'https://gw.alicdn.com/i4/TB1FwROmq61gK0jSZFlXXXDKFXa_.jpg_160x160Q30s100.jpg_.webp',
                    movietitle: '触不可及',
                    moviemark: '9.1',
                    moviedirect: '尼尔·博格',
                    moviestar: '布莱恩·科兰斯顿',
                    id: '3'
                },
            ],
        };
    }

    componentDidMount() {
        console.log(this.state.isShow)
        // let that = this;
        $.ajax({
            url: "http://localhost:9000/getindex",
            type: "GET",
            dataType: "text",
            success(data) {
                if (data) {
                    data = JSON.parse(data);
                    this.setState({
                        data1: data
                    })
                }
            },
            error() {

            }
        })
        setTimeout(() => {
            this.rData = this.genData();
            this.setState({
                dataSource: this.state.dataSource.cloneWithRows(this.rData),
                isLoading: false,
            });
        }, 600);
    }
    

    genData(pIndex = 0) {
        const dataBlob = {};
        for (let i = 0; i < this.state.NUM_ROWS; i++) {
            const ii = (pIndex * this.state.NUM_ROWS) + i;
            dataBlob[`${ii}`] = `row - ${ii}`;
        }
        return dataBlob;
    }
    onEndReached = (event) => {
        // load new data
        // hasMore: from backend data, indicates whether it is the last page, here is false

        if (this.state.isLoading && !this.state.hasMore) {
            return;
        }
        console.log('reach end', event);
        this.setState({ isLoading: true });
        setTimeout(() => {
            this.rData = { ...this.rData, ...this.genData(++this.pageIndex) };
            this.setState({
                dataSource: this.state.dataSource.cloneWithRows(this.rData),
                isLoading: false,
            });
        }, 1000);
    }

    render() {
        if (this.state.data1 != null) {
            console.log(this.state.data1);
            //所有的电影列表
            let movielist = this.state.data1;
            //已经上映的电影
            let list1 = [];
            //未上映的电影
            let list2 = [];
            let year = new Date().getFullYear();
            let month = new Date().getMonth() + 1;
            let date = new Date().getDate();
            if (movielist != null) {
                for (let i = 0; i < movielist.length; i++) {
                    let movieYear = Number(movielist[i].moviedate.substring(0, 4));
                    let movieMonth = Number(movielist[i].moviedate.substring(5, 7));
                    let moviedate = Number(movielist[i].moviedate.substring(8, 10)) + 1;
                    if (movieYear > year) {
                        list2.push(movielist[i]);
                    }
                    else if (movieYear < year) {
                        list1.push(movielist[i]);
                    }
                    else {
                        if (movieMonth > month) {
                            list2.push(movielist[i]);
                        }
                        else if (movieMonth < month) {
                            list1.push(movielist[i]);
                        }
                        else {
                            if (moviedate > date) {
                                list2.push(movielist[i]);
                            }
                            else {
                                list1.push(movielist[i]);
                            }
                        }
                    }
                }
            }
            console.log(list1);
            console.log(list2)
            if (this.state.isShow == true) {
                this.setState = ({
                    data: list1
                })
            }
            else {
                this.setState = ({
                    data: list2
                })
            }
        }
        const separator = (sectionID, rowID) => (
            <div
                key={`${sectionID}-${rowID}`}
                style={{
                    backgroundColor: '#F5F5F9',
                    height: 8,
                    borderTop: '1px solid #ECECED',
                    borderBottom: '1px solid #ECECED',
                }}
            />
        );
        let index = 0;
        const row = (rowData, sectionID, rowID) => {
            if (index <= this.state.data.length) {
                const obj = this.state.data[index++];
                return (
                    <div key={rowID} style={{ padding: '0 15px' }}>
                        <div
                            style={{
                                lineHeight: '50px',
                                color: '#000',
                                fontSize: 18,
                                borderBottom: '1px solid #F6F6F6',
                            }}
                        >{obj.movietitle}</div>
                        <div style={{ display: '-webkit-box', display: 'flex', padding: '15px 0', position: 'relative' }}>
                            <img style={{ height: '100px', marginRight: '15px' }} src={obj.movieimage} alt="" />
                            <div style={{ lineHeight: 1 }}>
                                <div style={{ marginBottom: '15px', marginTop: '15px', fantSize: '13px', color: '#666', width: 'auto', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>评分：{obj.moviemark}</div>
                                <div style={{ marginBottom: '15px', marginTop: '15px', fantSize: '13px', color: '#666', width: 'auto', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>导演：{obj.moviedirect}</div>
                                <div style={{ marginBottom: '15px', marginTop: '15px', fantSize: '13px', color: '#666', width: 'auto', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>主演：{obj.moviestar}</div>

                            </div>

                            <IsPurser isShow={this.props.isShow}></IsPurser>
                        </div>
                    </div>
                );
            }
        };
        return (
            <ListView
                ref={el => this.lv = el}
                initialListSize={this.state.data.length}
                dataSource={this.state.dataSource}
                renderHeader={() => <span>header</span>}
                renderFooter={() => (<div style={{ padding: 30, textAlign: 'center' }}>
                    {this.state.isLoading ? 'Loading...' : 'Loaded'}
                </div>)}
                renderRow={row}
                renderSeparator={separator}
                className="am-list"
                pageSize={4}
                useBodyScroll
                onScroll={() => { console.log('scroll'); }}
                scrollRenderAheadDistance={500}
                onEndReached={this.onEndReached}
                onEndReachedThreshold={10}
            />
        );
    }
}
